create table todoitems (completed boolean, duration numeric(21,0), hatelevel tinyint check (hatelevel between 0 and 4), importance tinyint check (importance between 0 and 4), deadline timestamp(6) with time zone, id bigint generated by default as identity, description varchar(255), title varchar(255), primary key (id));
