create table todoitems (completed boolean not null, duration numeric(21,0) not null, hatelevel tinyint not null check (hatelevel between 0 and 4), importance tinyint not null check (importance between 0 and 4), deadline timestamp(6) with time zone not null, id bigint generated by default as identity, description varchar(255), title varchar(255) not null, primary key (id));
